css:
/* Base Styles */

.sb-table {
  border: 1px solid #fff;
  font-family: -apple-system, BlinkMacSystemFont, sans-serif;
  text-align: left;
  font-size: 14px;
  border-collapse: collapse;
}

.sb-table th, .sb-table td {
  padding: 10px;
}
.sb-table th {
  border-right: 1px solid #fff;
  border-bottom: 1px solid #fff;
}

.sb-table th:last-child {
  border-right: none;
}

.sb-table th.sortable {
  cursor: pointer;
}

.sb-table th .sort-icon {
  width: 1em;
  display: inline-block;
  visibility: hidden;
}
.sb-table th:hover .sort-icon {
  visibility: visible;
}

/* Light Theme (default) */

.sb-table {
  color: black;
  border-color: #191A1F;
}

.sb-table thead {
  background-color: #e6e6e6;
}

.sb-table th {
  border-right-color: #191A1F;
  border-bottom-color: #191A1F;
}

.sb-table tbody {
  background-color: #fafafa;
}

/* Dark Theme */

.sb-table.dark {
  color: #939CAA;
  border-color: #191A1F;
}

.sb-table.dark thead {
  background-color: #282C34;
}

.sb-table.dark th {
  border-right-color: #191A1F;
  border-bottom-color: #191A1F;
}

.sb-table.dark tbody {
  background-color: #31363F;
}

.sb-table.linter {
  color: #424243;
  font-size: 12px;
  border-color: #dbdbdc;
}
.sb-table.linter thead {
  background-color: #fafafa;
}
.sb-table.linter tbody {
  background-color: #ffffff;
}
.sb-table.linter td,
.sb-table.linter th {
  padding: 3px 10px;
}
.sb-table.linter td {
  min-width: 100px;
}
.sb-table.linter th {
  border-right-color: #dbdbdc;
  border-bottom-color: #dbdbdc;
}
.sb-table.linter td:nth-child(4),
.sb-table.linter td:nth-child(5) {
  cursor: pointer;
}
